  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/code/merge-translate/node_modules/.pnpm/@cdn.sheetjs.com+xlsx-0.19.3@xlsx-0.19.3.tgz/node_modules/xlsx/bin/node_modules:/mnt/d/code/merge-translate/node_modules/.pnpm/@cdn.sheetjs.com+xlsx-0.19.3@xlsx-0.19.3.tgz/node_modules/xlsx/node_modules:/mnt/d/code/merge-translate/node_modules/.pnpm/@cdn.sheetjs.com+xlsx-0.19.3@xlsx-0.19.3.tgz/node_modules:/mnt/d/code/merge-translate/node_modules/.pnpm/node_modules"
